<?php/** * Grp_Inquiry extension *  * NOTICE OF LICENSE *  * This source file is subject to the MIT License * that is bundled with this package in the file LICENSE.txt. * It is also available through the world-wide-web at this URL: * http://opensource.org/licenses/mit-license.php *  * @category       Grp * @package        Grp_Inquiry * @copyright      Copyright (c) 2014 * @license        http://opensource.org/licenses/mit-license.php MIT License *//** * Inquiry edit form tab * * @category    Grp * @package     Grp_Inquiry * @author      Grapes-solutions.com */class Grp_Inquiry_Block_Adminhtml_Inquiry_Edit_Tab_Form    extends Mage_Adminhtml_Block_Widget_Form {    /**     * prepare the form     * @access protected     * @return Inquiry_Inquiry_Block_Adminhtml_Inquiry_Edit_Tab_Form     * @author Grapes-solutions.com     */    protected function _prepareForm(){        $form = new Varien_Data_Form();        $form->setHtmlIdPrefix('inquiry_');        $form->setFieldNameSuffix('inquiry');        $this->setForm($form);        $fieldset = $form->addFieldset('inquiry_form', array('legend'=>Mage::helper('grp_inquiry')->__('Inquiry')));        $fieldset->addField('first_name', 'text', array(            'label' => Mage::helper('grp_inquiry')->__('First Name'),            'name'  => 'first_name',            'required'  => false,            'class' => '',        ));                 $fieldset->addField('last_name', 'text', array(            'label' => Mage::helper('grp_inquiry')->__('Last Name'),            'name'  => 'last_name',            'required'  => false,            'class' => '',        ));                  $fieldset->addField('email', 'text', array(            'label' => Mage::helper('grp_inquiry')->__('Email'),            'name'  => 'email',            'required'  => false,            'class' => '',        ));                  $fieldset->addField('phone', 'text', array(            'label' => Mage::helper('grp_inquiry')->__('Phone'),            'name'  => 'phone',            'required'  => false,            'class' => '',        ));                  $fieldset->addField('product_id', 'text', array(            'label' => Mage::helper('grp_inquiry')->__('Product Id'),            'name'  => 'product_id',            'required'  => false,            'class' => '',        ));                 $fieldset->addField('sku', 'text', array(            'label' => Mage::helper('grp_inquiry')->__('Sku'),            'name'  => 'sku',            'required'  => false,            'class' => '',        ));                        $fieldset->addField('inquiries', 'textarea', array(            'label' => Mage::helper('grp_inquiry')->__('Comment'),            'name'  => 'inquiries',            'required'  => false,            'class' => '',        ));                        $formValues = Mage::registry('current_inquiry')->getDefaultValues();        if (!is_array($formValues)){            $formValues = array();        }        if (Mage::getSingleton('adminhtml/session')->getInquiryData()){            $formValues = array_merge($formValues, Mage::getSingleton('adminhtml/session')->getInquiryData());            Mage::getSingleton('adminhtml/session')->setInquiryData(null);        }        elseif (Mage::registry('current_inquiry')){            $formValues = array_merge($formValues, Mage::registry('current_inquiry')->getData());        }        $form->setValues($formValues);        return parent::_prepareForm();    }}